import React from "react";

import '../index.css'

function Show(props){

    const year = props.year
    const title = props.title;
    const link = props.link;
    return <div className="image">

        <img src={`https://image.tmdb.org/t/p/w300_and_h450_bestv2/5KCVkau1HEl7ZzfST3OVLGmAx9k.jpg`} alt="image"/>
        <p>{title}</p>
        <p>{year}</p>
    </div>
}

export default Show;
